import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const Emissions = ({ country, setCountry }) => {
  const { id } = useParams();
  const [biomass, setBiomass] = useState();
  const [coal, setCoal] = useState();
  const [geothermal, setGeothermal] = useState();
  const [hydro, setHydro] = useState();
  const [nuclear, setNuclear] = useState();
  const [pet, setPet] = useState()

  //GAS
  useEffect(() => {
    const url = "https://beta3.api.climatiq.io/estimate";
    fetch(url, {
      method: "POST",
      headers: {
        Authorization: "Bearer 2WBP8XKDTTMMMSQBCX27SPSDD078",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        emission_factor: {
          activity_id: "electricity-energy_source_gas",
          region: id,
        },
        parameters: {
          money: 100,
          money_unit: "eur",
        },
      }),
    })
      .then((res) => res.json())
      .then((data) => data.co2e)
      .then((data) =>
        // console.log(data)
        setCountry(data)
      )
      .catch(function (error) {
        console.log(error);
      });
  }, []);
  let twoDecimal = parseFloat(country).toFixed(2);
  //BIOMASS
  useEffect(() => {
    const url = "https://beta3.api.climatiq.io/estimate";
    fetch(url, {
      method: "POST",
      headers: {
        Authorization: "Bearer 2WBP8XKDTTMMMSQBCX27SPSDD078",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        emission_factor: {
          activity_id: "electricity-energy_source_biomass_waste",
          region: id,
        },
        parameters: {
          money: 100,
          money_unit: "eur",
        },
      }),
    })
      .then((res) => res.json())
      .then((data) => data.co2e)
      .then((data) =>
        // console.log(data)
        setBiomass(data)
      )
      .catch(function (error) {
        console.log(error);
      });
  }, []);
  let threeDecimal = parseFloat(biomass).toFixed(2);

  //COAL
  useEffect(() => {
    const url = "https://beta3.api.climatiq.io/estimate";
    fetch(url, {
      method: "POST",
      headers: {
        Authorization: "Bearer 2WBP8XKDTTMMMSQBCX27SPSDD078",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        emission_factor: {
          activity_id: "electricity-energy_source_coal",
          region: id,
        },
        parameters: {
          money: 100,
          money_unit: "eur",
        },
      }),
    })
      .then((res) => res.json())
      .then((data) => data.co2e)
      .then((data) =>
        // console.log(data)
        setCoal(data)
      )
      .catch(function (error) {
        console.log(error);
      });
  }, []);
  let fourDecimal = parseFloat(coal).toFixed(2);

  //Geothermal
  useEffect(() => {
    const url = "https://beta3.api.climatiq.io/estimate";
    fetch(url, {
      method: "POST",
      headers: {
        Authorization: "Bearer 2WBP8XKDTTMMMSQBCX27SPSDD078",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        emission_factor: {
          activity_id: "electricity-energy_source_geothermal",
          region: id,
        },
        parameters: {
          money: 100,
          money_unit: "eur",
        },
      }),
    })
      .then((res) => res.json())
      .then((data) => data.co2e)
      .then((data) =>
        // console.log(data)
        setGeothermal(data)
      )
      .catch(function (error) {
        console.log(error);
      });
  }, []);
  let fiveDecimal = parseFloat(coal).toFixed(2);

  //HYDRO
  useEffect(() => {
    const url = "https://beta3.api.climatiq.io/estimate";
    fetch(url, {
      method: "POST",
      headers: {
        Authorization: "Bearer 2WBP8XKDTTMMMSQBCX27SPSDD078",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        emission_factor: {
          activity_id: "electricity-energy_source_hydro",
          region: id,
        },
        parameters: {
          money: 100,
          money_unit: "eur",
        },
      }),
    })
      .then((res) => res.json())
      .then((data) => data.co2e)
      .then((data) =>
        // console.log(data)
        setHydro(data)
      )
      .catch(function (error) {
        console.log(error);
      });
  }, []);
  let sixDecimal = parseFloat(hydro).toFixed(2);

  //NUCLEAR
  useEffect(() => {
    const url = "https://beta3.api.climatiq.io/estimate";
    fetch(url, {
      method: "POST",
      headers: {
        Authorization: "Bearer 2WBP8XKDTTMMMSQBCX27SPSDD078",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        emission_factor: {
          activity_id: "electricity-energy_source_nuclear",
          region: id,
        },
        parameters: {
          money: 100,
          money_unit: "eur",
        },
      }),
    })
      .then((res) => res.json())
      .then((data) => data.co2e)
      .then((data) =>
        // console.log(data)
        setNuclear(data)
      )
      .catch(function (error) {
        console.log(error);
      });
  }, []);
  let sevenDecimal = parseFloat(nuclear).toFixed(2);

    //NUCLEAR
    useEffect(() => {
        const url = "https://beta3.api.climatiq.io/estimate";
        fetch(url, {
          method: "POST",
          headers: {
            Authorization: "Bearer 2WBP8XKDTTMMMSQBCX27SPSDD078",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            emission_factor: {
              activity_id: "electricity-energy_source_nuclear",
              region: id,
            },
            parameters: {
              money: 100,
              money_unit: "eur",
            },
          }),
        })
          .then((res) => res.json())
          .then((data) => data.co2e)
          .then((data) =>
            // console.log(data)
            setPet(data)
          )
          .catch(function (error) {
            console.log(error);
          });
      }, []);
      let eightDecimal = parseFloat(pet).toFixed(2);

  return (
    <div>
      {/* Natual Gas */}
      <div>
        {country !== undefined ? (
          <div>
            <h1>{id}</h1>
            <div className="gas">
              <p>Natural Gas: {twoDecimal} kg co2e per €100 </p>
            </div>
          </div>
        ) : (
          <h1>
            There is no emissions data on electricity generated by gas in {id}
          </h1>
        )}
      </div>
      {/* Biomass */}
      <div>
        <div>
          {biomass !== undefined ? (
            <div>
              <div className="gas">
                <p>Biomass: {threeDecimal} kg co2 equivalent per €100</p>
              </div>
            </div>
          ) : (
            <h1>
              There is no emissions data on electricity generated by biomass in
              {id}
            </h1>
          )}
        </div>
      </div>
      {/* Coal */}
      <div>
        <div>
          {coal !== undefined ? (
            <div>
              <div className="gas">
                <p> Coal: {fourDecimal} kg co2e per €100</p>{" "}
              </div>
            </div>
          ) : (
            <h1>
              There is no emissions data on electricity generated by biomass in
              {id}
            </h1>
          )}
        </div>
      </div>
      {/* Geothermal */}
      <div>
        <div>
          {geothermal !== undefined ? (
            <div>
              <div>
                <p> Geothermal: {fiveDecimal} kg co2e per €100</p>{" "}
              </div>
            </div>
          ) : (
            <h1>
              There is no emissions data on electricity generated by biomass in
              {id}
            </h1>
          )}
        </div>
      </div>
      {/* Hydro */}
      <div>
        <div>
          {hydro !== undefined ? (
            <div>
              <div>
                <p> Hydro: {sixDecimal} kg co2e per €100</p>{" "}
              </div>
            </div>
          ) : (
            <h1>
              There is no emissions data on electricity generated by biomass in
              {id}
            </h1>
          )}
        </div>
      </div>
      {/* NUCLEAR */}
      <div>
        <div>
          {hydro !== undefined ? (
            <div>
              <div>
                <p> Nuclear: {sevenDecimal} kg co2e per €100</p>{" "}
              </div>
            </div>
          ) : (
            <h1>
              There is no emissions data on electricity generated by biomass in
              {id}
            </h1>
          )}
        </div>
      </div>
    </div>
  );
};

export default Emissions;

//set id and country to an async function that are then declared in useEffect with arrow functions and then called within HTML
